import React from 'react';
import './App.css';

const Home = () => {
  // Datos generales que se pueden calcular y mostrar
  const generalStats = {
    totalRows: 3, // Número total de registros en la tabla
    averageAge: 25.7, // Edad promedio
    youngest: 'Gael', // Nombre del más joven
    oldest: 'Lalo', // Nombre del más viejo
  };

  return (
    <div className="app-container">
      <h1 className="title">Datos Generales</h1>

      <div className="stats-container">
        <div className="stat-card">
          <h3>Total de Registros</h3>
          <p>{generalStats.totalRows}</p>
        </div>

        <div className="stat-card">
          <h3>Edad Promedio</h3>
          <p>{generalStats.averageAge}</p>
        </div>

        <div className="stat-card">
          <h3>Más Joven</h3>
          <p>{generalStats.youngest}</p>
        </div>

        <div className="stat-card">
          <h3>Más Viejo</h3>
          <p>{generalStats.oldest}</p>
        </div>
      </div>

      {/* Botón para regresar al Tablon */}
      <div className="navigation-button">
        <button className="crud-button" onClick={() => window.location.App.js()}>
          Volver a Tablon
        </button>
      </div>
    </div>
  );
};

export default Home;
