import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom'; // Actualizado con Routes

const App = () => {
  const [data] = useState([
    { id: 1, name: 'De anda', age: 27 },
    { id: 2, name: 'Gael', age: 21 },
    { id: 3, name: 'Lalo', age: 29 },
  ]);
  const [search, setSearch] = useState('');
  const [filterAge, setFilterAge] = useState('');

  const filteredData = data.filter(
    (item) =>
      item.name.toLowerCase().includes(search.toLowerCase()) &&
      (filterAge ? item.age == filterAge : true)
  );

  return (
    <Router>
      <div className="app-container">
        {/* Barra de navegación */}
        <nav>
          <ul className="nav-links">
            <li><Link to="/">Inicio</Link></li>
            {/* Puedes añadir más enlaces a otras páginas aquí */}
          </ul>
        </nav>

        {/* Página principal con la tabla */}
        <Routes>
          <Route path="/" element={
            <>
              <h1 className="title">Tablon</h1>

              {/* Barra de búsqueda y filtro */}
              <div className="filters">
                <input
                  type="text"
                  placeholder="Buscar por nombre"
                  className="search-input"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                />
                <input
                  type="number"
                  placeholder="Filtrar por edad"
                  className="filter-input"
                  value={filterAge}
                  onChange={(e) => setFilterAge(e.target.value)}
                />
              </div>

              {/* Botones CRUD */}
              <div className="crud-buttons">
                <button className="crud-button">Crear</button>
                <button className="crud-button">Leer</button>
                <button className="crud-button">Actualizar</button>
                <button className="crud-button">Eliminar</button>
              </div>

              {/* Tabla */}
              <table className="styled-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Nombre</th>
                    <th>Edad</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.map((item) => (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.name}</td>
                      <td>{item.age}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </>
          } />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
