import React from 'react';
import './App.css';
import './Home.css';

const Home = () => {
  const data = [
    { id: 1, name: 'De anda', age: 27 },
    { id: 2, name: 'Gael', age: 21 },
    { id: 3, name: 'Lalo', age: 29 },
    { id: 4, name: 'Carlos', age: 32 },
    { id: 5, name: 'Andrea', age: 25 },
    { id: 6, name: 'Laura', age: 24 },
  ];

  {/* Botón para ir a Home.js */}
  <div className="navigation-button">
  <Link to="/home">
    <button className="crud-button">Ir a Home</button>
  </Link>
</div>

  return (
    <div className="home-container">
      <h1 className="title">Datos de la Tabla</h1>

      {/* Cuadros de datos */}
      <div className="data-cards">
        {data.map((item) => (
          <div className="card" key={item.id}>
            <h2 className="card-title">{item.name}</h2>
            <p className="card-info">ID: {item.id}</p>
            <p className="card-info">Edad: {item.age}</p>
          </div>
        ))}
      </div>
    </div>


  );
};

export default Home;
